import { requestConstants } from '../_constants';

export const requestActions = {
  inProcess,
  completed,
  dispatchRequestInProcess
};

function inProcess(message) {
  return { type: requestConstants.IN_PROCESS, message };
}

function completed(message) {
  return { type: requestConstants.COMPLETED, message };
}

function dispatchRequestInProcess(message) {
  return (dispatch) => {
    return dispatch({type: requestConstants.IN_PROCESS, message})
  };
}
