import React from 'react';

export const ValidatedInput = ({ error, className, ...props }) => {
  return (
    <div>
      <input className={`${className} ${error ? 'is-invalid': ''}`} {...props} />
      <div className="invalid-feedback"> {error} </div>
    </div>
  );
};
export const ValidatedCheckbox = ({ error, className, children, ...props }) => {
  return (
    <div>
      <input className={`${className} ${error ? 'is-invalid': ''}`} {...props} />
      {children}
      <div className="invalid-feedback mb-1"> {error} </div>
    </div>
  );
};
export const ValidatedSelect = ({ error, className, children, ...props }) => {
  return (
    <div>
      <select className={`${className} ${error ? 'is-invalid': ''}`} {...props} >
        {children}
      </select>
      <div className="invalid-feedback mb-1"> {error} </div>
    </div>
  );
};
export const ValidatedTextarea = ({ error, className, children, ...props }) => {
  return (
    <div>
      <textarea className={`${className} ${error ? 'is-invalid': ''}`} {...props} />
      <div className="invalid-feedback mb-1"> {error} </div>
    </div>
  );
};