import { appStatusConstants } from '../_constants';
import * as R from 'ramda';

const initialState = {
  profileSidebar: false,
  mobMenu: false
};

export function appStatus(state = initialState, {type, payload}) {
  switch (type) {
    case appStatusConstants.PROFILE_SIDEBAR:
      return R.merge(state, payload);
    default:
      return state
  }
}