import axios from 'axios';
import requestsUrl from 'projConfig'
import * as R from 'ramda'

export const tradePlansService = {
  fetch,
  lastOperations
};

function fetch() {
  return axios.get(requestsUrl.plans)
    .then(response => {
      if (response.status !== 200) {
        return Promise.reject(response.statusText);
      }
      return response.data;
    })
    .then(plans => {
      return Promise.resolve(plans);
    });
}

function lastOperations() {
  return axios.post(requestsUrl.lastOperations)
    .then(response => {
      if (response.status !== 200) {
        return Promise.reject(response.statusText);
      }
      return response.data;
    })
    .then(plans => {
      return Promise.resolve(plans);
    });
}